import React, { useState } from "react";

import { TextField } from '@material-ui/core';

const InputSearch = () => {
    const [ value, setValue ] = useState('');

    const handleChange = ({ target }) => {
        setValue(target.value)
    }

    return (
        <TextField
            id="outlined-name"
            label="Search"
            value={ value }
            onChange={ handleChange }
            variant="outlined"
        />
    )
}

export default InputSearch;
