import preloader from '../../img/circles.svg'

const styles = theme => ({
    card: {
        background: `url(${preloader}) no-repeat center `,
    },

    img: {
        objectFit: 'fill',
        width: '100%',
        height: '100%',
        borderRadius: 8,
        transition: '0.5s',
        "&:hover": {
            transform: 'scale(0.9)',
            cursor: 'pointer'
        } ,
    }
})

export default styles;
