import React, { useState } from "react";
import { InputSearch } from "..";
import { getImageSearch } from '../../api/appi'
import { ImgCard } from "..";
import { makeStyles } from '@material-ui/core/styles';
import styles from './styles';


const useStyles = makeStyles(styles);

const ImgList = () => {
    const classes = useStyles();
    const [images, setImages] = useState([]);


    const imageSearch = (value) => {
        setImages([])
        console.log(value, 'value');
        getImageSearch(value)
        .then(result => setImages(result.results))
    };

    console.log(images);

    const handlerOnClick = () => {

    }

    return (
        <div className={ classes.test } >
            <InputSearch imageSearch={ imageSearch } />
            { images.length > 0
                &&(
                <div className={ classes.wrapper } >
                        { images.map(image => <ImgCard image={ image } key={ image.id } />) }
                        <button className={ classes.wrapper } onClick={ handlerOnClick }>

                        </button>
                </div>)
            }
        </div>
    )
}

export default ImgList;
