import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import styles from './styles';
import {
    Dialog,
    IconButton,
    Button,
} from '@material-ui/core';


const useStyles = makeStyles(styles);

const ImgCard = props => {
    const classes = useStyles();
    const { image, imgSize } = props;
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };

    const className = () => {
        const size = imgSize === 'small' ? true : false;

        return size ? classes.smallCard : classes.bigCard
    }

    const classNameImg = () => {
        const size = imgSize === 'small' ? true : false;

        return size ? classes.img : classes.bigImg
    }

    return (
        <div className={ className() }>
            <img src={ image.links.download } onClick={ handleClickOpen } className={ classNameImg() } alt={'test'} />
            <Dialog onClose={handleClose} aria-labelledby="customized-dialog-title" open={open}>
                <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
                    <CloseIcon />
                </IconButton>
                <div>
                    <Button autoFocus onClick={handleClose} color="primary">
                        Save changes
                    </Button>
                </div>

            </Dialog>
        </div>
    )
}

export default ImgCard;
