import React, { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import styles from './styles';
import {
    Dialog,
    IconButton,
    Button,
} from '@material-ui/core';
import CloseIcon from '@material-ui/icons/Close';


const useStyles = makeStyles(styles);

const ImgCard = props => {
    const classes = useStyles();
    const { image, imgSize } = props;
    const [open, setOpen] = useState(false);
    const [fullWidth, setFullWidth] = useState(true);
    const [maxWidth, setMaxWidth] = useState('lg');

    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };

    const className = () => {
        const size = imgSize === 'small' ? true : false;

        return size ? classes.smallCard : classes.bigCard
    }

    const classNameImg = () => {
        const size = imgSize === 'small' ? true : false;

        return size ? classes.img : classes.bigImg
    }

    return (
        <div className={ className() }>
            <img src={ image.links.download } onClick={ handleClickOpen } className={ classNameImg() } alt={'test'} />
            <Dialog
                className={ classes.dialog }
                onClose={handleClose}
                aria-labelledby="customized-dialog-title"
                open={open}
                fullWidth={fullWidth}
                maxWidth={'lg'}
            >
                <div className={ classes.top } >
                    <IconButton aria-label="close" className={ classes.closeButton } onClick={handleClose}>
                        <CloseIcon />
                    </IconButton>
                </div>
                <div className={ classes.content }>
                    <Button autoFocus onClick={ handleClose } color="primary">
                        Save changes
                    </Button>
                </div>

            </Dialog>
        </div>
    )
}

export default ImgCard;
