import preloader from '../../img/circles.svg'
const card = {
        width: 200,
        height: 200,
        background: `url(${preloader}) no-repeat center `,
    }

const styles = theme => ({
    card: {
        width: 200,
        height: 200,
        background: `url(${preloader}) no-repeat center `,
    },

    smallCard: {
        ...card
    },

    bigCard: {
        ...card,
        width: 430,
        height: 430,
    },

    img: {
        objectFit: 'fill',
        width: '100%',
        height: '100%',
        borderRadius: 8,
        transition: '0.5s',
        "&:hover": {
            transform: 'scale(0.9)',
            cursor: 'pointer'
        } ,
    }
})

export default styles;
